import React from 'react';
import { useMarketData } from '../../hooks/useMarketData';
import './Charts.css';

const MarketOverview = ({ symbols = ['AAPL', 'MSFT', 'GOOGL'], onSymbolSelect }) => {
  return (
    <div className="market-overview">
      <h3>Market Overview</h3>
      <div className="symbols-grid">
        {symbols.map(symbol => (
          <SymbolCard 
            key={symbol} 
            symbol={symbol} 
            onSelect={onSymbolSelect}
          />
        ))}
      </div>
    </div>
  );
};

const SymbolCard = ({ symbol, onSelect }) => {
  const { data, loading, error, currentPrice, priceChangePercent } = useMarketData(symbol, '1d');
  
  if (loading) return (
    <div className="symbol-card loading" onClick={() => onSelect(symbol)}>
      <div className="loading-spinner small"></div>
      <span>Loading {symbol}...</span>
    </div>
  );
  
  if (error) return (
    <div className="symbol-card error" onClick={() => onSelect(symbol)}>
      <span>❌</span>
      <span>{symbol}</span>
      <span>Error</span>
    </div>
  );

  const isPositive = priceChangePercent >= 0;

  return (
    <div className="symbol-card" onClick={() => onSelect(symbol)}>
      <div className="symbol-header">
        <span className="symbol">{symbol}</span>
        <span className={`price-change ${isPositive ? 'positive' : 'negative'}`}>
          {isPositive ? '↗' : '↘'} {Math.abs(priceChangePercent).toFixed(2)}%
        </span>
      </div>
      <div className="symbol-price">${currentPrice?.toFixed(2) || '0.00'}</div>
      <div className="symbol-details">
        {data && data.length > 0 && (
          <>
            <div>High: ${data[0]?.high?.toFixed(2) || '0.00'}</div>
            <div>Low: ${data[0]?.low?.toFixed(2) || '0.00'}</div>
            <div>Vol: {data[0]?.volume ? (data[0].volume / 1000000).toFixed(1) + 'M' : 'N/A'}</div>
          </>
        )}
      </div>
    </div>
  );
};

export default MarketOverview;
