import React, { useState, useEffect, useCallback } from 'react';
import { api } from '../../services/api';
import './Portfolio.css';

const PortfolioManager = () => {
  const [portfolio, setPortfolio] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Wrap loadPortfolio in useCallback to prevent infinite re-renders
  const loadPortfolio = useCallback(async () => {
    try {
      setLoading(true);
      const data = await api.getPortfolio();
      setPortfolio(data);
    } catch (err) {
      setError(err.message);
    } finally {
      setLoading(false);
    }
  }, []); // Empty dependency array since we don't use any external variables

  useEffect(() => {
    loadPortfolio();
  }, [loadPortfolio]); // Now loadPortfolio is included in dependencies

  const executeTrade = async (symbol, action, quantity, price) => {
    try {
      // This would be implemented to execute actual trades
      // For now, we'll just simulate it
      console.log(`Executing trade: ${action} ${quantity} ${symbol} @ ${price}`);
      
      // Reload portfolio to reflect changes
      await loadPortfolio();
    } catch (err) {
      setError(err.message);
    }
  };

  if (loading) return <div className="portfolio-loading">Loading portfolio...</div>;
  if (error) return <div className="portfolio-error">Error: {error}</div>;
  if (!portfolio) return <div>No portfolio found</div>;

  const totalValue = portfolio.calculateValue ? portfolio.calculateValue() : portfolio.balance;
  const totalReturn = portfolio.calculateReturn ? portfolio.calculateValue() : 0;

  return (
    <div className="portfolio-manager">
      <div className="portfolio-header">
        <h2>Portfolio Overview</h2>
        <div className="portfolio-summary">
          <div className="summary-item">
            <span className="label">Total Value:</span>
            <span className="value">${totalValue.toFixed(2)}</span>
          </div>
          <div className="summary-item">
            <span className="label">Cash:</span>
            <span className="value">${portfolio.balance.toFixed(2)}</span>
          </div>
          <div className="summary-item">
            <span className="label">Total Return:</span>
            <span className={`value ${totalReturn >= 0 ? 'positive' : 'negative'}`}>
              {totalReturn >= 0 ? '+' : ''}{totalReturn.toFixed(2)}%
            </span>
          </div>
        </div>
      </div>

      <div className="positions-section">
        <h3>Positions</h3>
        {portfolio.positions && portfolio.positions.length === 0 ? (
          <p>No positions yet. Start trading to build your portfolio.</p>
        ) : (
          <div className="positions-grid">
            {portfolio.positions && portfolio.positions.map(position => (
              <PositionCard
                key={position.symbol}
                position={position}
                onTrade={executeTrade}
              />
            ))}
          </div>
        )}
      </div>

      <div className="performance-section">
        <h3>Performance Metrics</h3>
        <div className="metrics-grid">
          <div className="metric-card">
            <span className="metric-label">Sharpe Ratio</span>
            <span className="metric-value">{portfolio.performance?.sharpeRatio?.toFixed(2) || '0.00'}</span>
          </div>
          <div className="metric-card">
            <span className="metric-label">Max Drawdown</span>
            <span className="metric-value">{portfolio.performance?.maxDrawdown?.toFixed(2) || '0.00'}%</span>
          </div>
          <div className="metric-card">
            <span className="metric-label">Daily Return</span>
            <span className="metric-value">{portfolio.performance?.dailyReturn?.toFixed(2) || '0.00'}%</span>
          </div>
        </div>
      </div>
    </div>
  );
};

const PositionCard = ({ position, onTrade }) => {
  const unrealizedPnl = position.currentPrice 
    ? (position.currentPrice - position.entryPrice) * position.quantity
    : 0;
  
  const unrealizedPnlPercent = position.currentPrice && position.entryPrice
    ? ((position.currentPrice - position.entryPrice) / position.entryPrice) * 100
    : 0;

  return (
    <div className="position-card">
      <div className="position-header">
        <span className="symbol">{position.symbol}</span>
        <span className={`pnl ${unrealizedPnl >= 0 ? 'positive' : 'negative'}`}>
          {unrealizedPnl >= 0 ? '+' : ''}{unrealizedPnlPercent.toFixed(2)}%
        </span>
      </div>
      
      <div className="position-details">
        <div>Quantity: {position.quantity}</div>
        <div>Avg Price: ${position.entryPrice?.toFixed(2) || '0.00'}</div>
        {position.currentPrice && (
          <div>Current: ${position.currentPrice.toFixed(2)}</div>
        )}
        <div>Value: ${(position.quantity * (position.currentPrice || position.entryPrice || 0)).toFixed(2)}</div>
      </div>
      
      <div className="position-actions">
        <button 
          className="btn-buy"
          onClick={() => onTrade(position.symbol, 'BUY', 1, position.currentPrice || position.entryPrice)}
        >
          Buy
        </button>
        <button 
          className="btn-sell"
          onClick={() => onTrade(position.symbol, 'SELL', position.quantity, position.currentPrice || position.entryPrice)}
        >
          Sell
        </button>
      </div>
    </div>
  );
};

export default PortfolioManager;
