import React, { useState, useEffect } from 'react';
import { useApp } from '../../contexts/AppContext';
import { useSignals } from '../../hooks/useSignals';
import { useMarketData } from '../../hooks/useMarketData';
import MarketOverview from '../DataVisualization/MarketOverview';
import './Dashboard.css';

const Dashboard = () => {
  const { userPreferences, addAlert } = useApp();
  const { signals, loading: signalsLoading, error: signalsError } = useSignals({ limit: 10 });
  const [selectedSymbol, setSelectedSymbol] = useState('AAPL');
  const { data: marketData, loading: marketLoading } = useMarketData(selectedSymbol, '1d');
  
  const [dashboardStats, setDashboardStats] = useState({
    totalSignals: 0,
    strongSignals: 0,
    buySignals: 0,
    sellSignals: 0,
    marketStatus: 'loading'
  });

  // Calculate dashboard statistics
  useEffect(() => {
    if (signals && Array.isArray(signals)) {
      const strongSignals = signals.filter(s => s.strength > 0.7);
      const buySignals = signals.filter(s => s.signalType === 'BUY');
      const sellSignals = signals.filter(s => s.signalType === 'SELL');

      setDashboardStats({
        totalSignals: signals.length,
        strongSignals: strongSignals.length,
        buySignals: buySignals.length,
        sellSignals: sellSignals.length,
        marketStatus: marketData && marketData.length > 0 ? 'active' : 'loading'
      });
    }
  }, [signals, marketData]);

  // Handle errors
  useEffect(() => {
    if (signalsError) {
      addAlert({
        type: 'error',
        message: `Failed to load signals: ${signalsError}`
      });
    }
  }, [signalsError, addAlert]);

  // Get default symbols from user preferences or use defaults
  const defaultSymbols = userPreferences?.defaultSymbols || ['AAPL', 'MSFT', 'GOOGL', 'AMZN', 'TSLA'];

  // Strong signals for display
  const strongSignals = signals.filter(s => s.strength > 0.7).slice(0, 5);

  return (
    <div className="dashboard">
      <div className="dashboard-header">
        <h1>Financial Signals Dashboard</h1>
        <p>Real-time market analysis and trading signals</p>
        <div className="dashboard-controls">
          <select 
            value={selectedSymbol} 
            onChange={(e) => setSelectedSymbol(e.target.value)}
            className="symbol-selector"
          >
            {defaultSymbols.map(symbol => (
              <option key={symbol} value={symbol}>{symbol}</option>
            ))}
          </select>
          <button 
            onClick={() => window.location.reload()} 
            className="refresh-btn"
          >
            Refresh
          </button>
        </div>
      </div>

      {/* Statistics Grid */}
      <div className="stats-grid">
        <StatCard 
          icon="📊"
          title="Total Signals"
          value={dashboardStats.totalSignals}
          trend={dashboardStats.totalSignals > 0 ? 'up' : 'neutral'}
        />
        <StatCard 
          icon="🚀"
          title="Strong Signals"
          value={dashboardStats.strongSignals}
          trend={dashboardStats.strongSignals > 0 ? 'up' : 'neutral'}
          highlight={true}
        />
        <StatCard 
          icon="📈"
          title="Buy Signals"
          value={dashboardStats.buySignals}
          trend="up"
          type="buy"
        />
        <StatCard 
          icon="📉"
          title="Sell Signals"
          value={dashboardStats.sellSignals}
          trend="down"
          type="sell"
        />
      </div>

      {/* Main Content Area */}
      <div className="dashboard-content">
        {/* Market Overview Section */}
        <div className="market-section">
          <MarketOverview 
            symbols={defaultSymbols}
            onSymbolSelect={setSelectedSymbol}
          />
        </div>

        {/* Recent Signals Section */}
        <div className="signals-section">
          <div className="card">
            <div className="card-header">
              <h2>Recent Strong Signals</h2>
              <span className="badge">{dashboardStats.strongSignals} signals</span>
            </div>
            
            {signalsLoading ? (
              <div className="loading-state">
                <div className="spinner"></div>
                <p>Loading signals...</p>
              </div>
            ) : signalsError ? (
              <div className="error-state">
                <p>⚠️ Unable to load signals</p>
                <small>{signalsError}</small>
              </div>
            ) : strongSignals.length === 0 ? (
              <div className="empty-state">
                <p>No strong signals detected in the last 24 hours</p>
                <small>Check back later for new trading opportunities</small>
              </div>
            ) : (
              <div className="signals-list">
                {strongSignals.map(signal => (
                  <SignalCard key={signal._id || signal.id} signal={signal} />
                ))}
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Quick Actions */}
      <div className="quick-actions">
        <h3>Quick Actions</h3>
        <div className="action-buttons">
          <button className="action-btn primary">Run Market Scan</button>
          <button className="action-btn secondary">View Portfolio</button>
          <button className="action-btn secondary">Backtest Strategy</button>
        </div>
      </div>
    </div>
  );
};

// Stat Card Component
const StatCard = ({ icon, title, value, trend, type, highlight }) => {
  return (
    <div className={`stat-card ${highlight ? 'highlight' : ''} ${type || ''}`}>
      <div className="stat-icon">{icon}</div>
      <div className="stat-content">
        <h3>{title}</h3>
        <p className="stat-value">{value}</p>
        <div className={`stat-trend ${trend}`}>
          {trend === 'up' ? '↗' : trend === 'down' ? '↘' : '→'} 
          {trend !== 'neutral' ? ' Today' : ' Stable'}
        </div>
      </div>
    </div>
  );
};

// Signal Card Component
const SignalCard = ({ signal }) => {
  const getSignalIcon = (signalType) => {
    switch (signalType) {
      case 'BUY': return '🟢';
      case 'SELL': return '🔴';
      default: return '⚪';
    }
  };

  return (
    <div className="signal-card">
      <div className="signal-header">
        <span className="signal-icon">{getSignalIcon(signal.signalType)}</span>
        <span className="symbol">{signal.symbol}</span>
        <span className={`signal-type ${signal.signalType.toLowerCase()}`}>
          {signal.signalType}
        </span>
        <span className="strength-badge">
          Strength: {(signal.strength * 100).toFixed(0)}%
        </span>
      </div>
      
      <div className="signal-body">
        <p className="signal-explanation">
          {signal.explanation || `Strong ${signal.signalType.toLowerCase()} signal detected`}
        </p>
        
        <div className="signal-meta">
          <span className="source">Source: {signal.source || 'analysis'}</span>
          <span className="confidence">
            Confidence: {(signal.confidence * 100).toFixed(0)}%
          </span>
        </div>
      </div>
      
      <div className="signal-footer">
        <span className="timestamp">
          {new Date(signal.generatedAt).toLocaleString()}
        </span>
        {signal.expiration && (
          <span className="expiration">
            Expires: {new Date(signal.expiration).toLocaleTimeString()}
          </span>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
